import axios from 'axios';

const API_BASE = 'http://localhost:5000'; // or your deployed backend

export const fetchRoutes = (params = {}) =>
  axios.get(`${API_BASE}/routes`, { params });

export const fetchRouteStops = (id: number) =>
  axios.get(`${API_BASE}/routes/${id}/stops`);

export const searchStops = (name: string) =>
  axios.get(`${API_BASE}/stops/search`, { params: { name } });
